/* Reset & standardize default styles */
@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css") layer;

/* Design tokens (apply to components) */
:root {
  /* Custom theme options */
  --color-accent: black;
  --color-accent2: #999999;
  --color-shade: #2d3339;

  --inner-max-width: 800px;

  /* Base values */
  --box-shadow: 0px 2px 8px rgba(0, 0, 0, 0.1);
  --border-radius: 2px;
  --border-color: #e0e1e1;
  --body-font: "Merriweather", serif;
  --heading-font: "Inter", sans-serif;
}

/* Root element (use instead of `body`) */
#page {
  font-family: var(--heading-font);
  color: #111;
  line-height: 1.5;
  font-size: 1.125rem;
  background: white;
}

/* Elements */
.section-container {
  max-width: 1150px;
  margin: 0 auto;
  padding: 5rem 2rem;
}

a.link {
  line-height: 1.3;
  border-bottom: 2px solid var(--color-accent);
  transform: translateY(-2px); /* move link back into place */
  transition: var(--transition, 0.1s border);

  &:hover {
    border-color: transparent;
  }
}

.heading {
  font-size: clamp(2rem, 10vw, 3rem);
  line-height: 1.1;
  font-weight: 600;
  letter-spacing: -0.15rem;
}

.button {
  color: white;
  background: var(--color-accent, rebeccapurple);
  border-radius: 0;
  padding: 1px 24px;
  transition: var(--transition, 0.1s box-shadow);
  border: 0; /* reset */
  border-radius: 0.25rem;
  font-size: 1rem;

  &:hover {
    box-shadow: 0 0 0 2px var(--color-accent, rebeccapurple);
  }

  &.inverted {
    background: transparent;
    color: var(--color-accent, rebeccapurple);
  }
}

header {
  color: var(--color-white, white);
  position: relative;
  overflow: hidden;
  background: var(--color-background);

  &.simple {
    background: var(--color-background);
  }
}

img {
  position: absolute;
  inset: 0;
  height: 100%;
  width: 100%;
  object-fit: cover;
  z-index: 0;
}

.heading-group {
  background: #12121260; /* overlay */
  display: grid;
  place-items: center;
  padding-top: 12rem;
  padding-bottom: 12rem;
  position: relative;
  gap: 1rem;

  .heading {
    font-size: clamp(2rem, 10vw, 3.5rem);
  }

  .subheading {
    place-self: center;
    font-size: 1.125rem;
    line-height: 1.3;
    color: var(--color-inverted);
  }
}

.buttons {
  margin-top: 1rem;
  display: flex;
  justify-content: center;
  gap: 1rem;

  .button {
    background: transparent;
    color: var(--color-inverted);
    border: 1.5px solid white;
  }
}

svg {
  width: 100%;
  background: #183b56a6;
  position: relative;
  fill: var(--background);
}

.section-container { /* Defined in Site CSS */
  padding: 2rem 2rem 1rem 2rem;
}

hr{
  border-color: #F2F2F2;
}

.footer { 
  display: flex;
  justify-content: space-between;
  flex-direction: start column;
  gap: 1rem;
  padding-top: 1rem;
  padding-bottom: 1rem;
}

.copyright {
  font-size: 0.75rem; 
  color: var(--color-text-secondary, #2D3339); 
}

.primo {
  font-weight: 600;
  font-size: 0.75rem;
  a {
    text-decoration: underline;  
  }
}

ul {
  display: flex;
  gap: 1.5rem;
  font-size: 1.5rem;
  list-style: none;

  a {
    transition: var(--transition);

    &:hover {
      color: var(--color-accent);
    }
  }
}

@media (min-width: 600px) {
  ul {
    font-size: 1.25rem;
  }
}

