.section-container {
  /* Defined in Site CSS */
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding-block: 2rem;
}

.logo {
  font-size: 1.5rem;
  font-weight: 600;
  line-height: 1.2;
}

.desktop-nav {
  display: none;
  align-items: center;
  justify-content: space-between;
  width: 100%;

  nav {
    display: flex;
    gap: 1.75rem;

    a.link {
      /* Defined in Site CSS */
      border-color: transparent;
      transition: border-color 0.1s;
      font-size: 0.95rem;
      font-weight: 500;


      &:hover, .active {
        border-color: initial;
      }
    }
  }

  @media (min-width: 800px) {
    display: flex;
  }
}

.mobile-nav {
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 100%;
  font-weight: 500;

  @media (min-width: 800px) {
    display: none;
  }
}

/* Mobile navigation popup */
#popup {
  font-size: 1.25rem;
  display: grid;
  gap: 1rem;
  position: absolute;
  text-align: center;
  background-color: var(--background-color, white);
  box-shadow: var(--box-shadow, 0px 4px 30px rgba(0, 0, 0, 0.1));
  border-radius: var(--border-radius);
  inset: 0.5rem;
  bottom: initial;
  padding: 2rem;
  z-index: 1;

  button#close {
    position: absolute;
    right: 1rem;
    top: 1rem;
  }
}

.section-container {
  max-width: var(--inner-max-width, 900px);
}

h2 {
  font-size: 1rem;
  line-height: 1.1;
  font-weight: 600;
  margin-bottom: 4rem;
  letter-spacing: -0.02rem;
  padding: .5rem 0;
  border-bottom: 1px solid #f0f0f0;
}

ul.items {
  display: grid;
  gap: 4rem;
}

li {
  display: grid;
  gap: 2rem;

  .post-info {
    .title {
      display: block;
      font-size: 1.3rem;
      font-weight: 600;
      margin-bottom: 0.75rem;
      line-height: 1.2;
      letter-spacing: -0.02rem;
      text-decoration: underline transparent;
      transition: 0.1s text-decoration;

      &:hover {
        text-decoration-color: initial;
      }
    }

    .description {
      font-size: 1rem;
      margin-bottom: 0.5rem;
      font-family: var(--body-font);
      line-height: 1.4;
      font-weight: 300;
      color: #2d3339;
    }

    .date {
      font-size: 1rem;
      font-weight: 400;
      color: var(--color-accent2);
      font-weight: 400;
    }
  }

  img {
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }
}

@media (min-width: 600px) {
  li {
    grid-template-columns: 3fr 1fr;
    gap: 3rem;
  }
}
